import React, { useState } from 'react';
import { Plus, MessageSquare, Trash2, Settings as SettingsIcon, User, Edit2, Check, X, LogOut } from 'lucide-react';
import Profile from './Profile';
import SettingsModal from './Settings';

const Sidebar = ({ 
  chats, 
  currentChatId, 
  onChatSelect, 
  onCreateNewChat, 
  onDeleteChat, 
  onRenameChat,
  isOpen, 
  onToggle,
  onSignOut
}) => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);
  const [editingChatId, setEditingChatId] = useState(null);
  const [editTitle, setEditTitle] = useState('');

  const handleDeleteChat = (chatId, e) => {
    e.stopPropagation();
    if (showDeleteConfirm === chatId) {
      onDeleteChat(chatId);
      setShowDeleteConfirm(null);
    } else {
      setShowDeleteConfirm(chatId);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = (now - date) / (1000 * 60 * 60);

    if (diffInHours < 24) {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else if (diffInHours < 168) { // 7 days
      return date.toLocaleDateString([], { weekday: 'short' });
    } else {
      return date.toLocaleDateString([], { month: 'short', day: 'numeric' });
    }
  };

  const handleEditChat = (chatId, currentTitle, e) => {
    e.stopPropagation();
    setEditingChatId(chatId);
    setEditTitle(currentTitle);
  };

  const handleSaveEdit = (chatId) => {
    if (editTitle.trim()) {
      onRenameChat(chatId, editTitle.trim());
    }
    setEditingChatId(null);
    setEditTitle('');
  };

  const handleCancelEdit = () => {
    setEditingChatId(null);
    setEditTitle('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSaveEdit(editingChatId);
    } else if (e.key === 'Escape') {
      handleCancelEdit();
    }
  };

  return (
    <>
      {/* Mobile overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={onToggle}
        />
      )}

      {/* Sidebar */}
      <div 
        className={`
          fixed lg:static inset-y-0 left-0 z-50 w-80 border-r
          transform transition-transform duration-300 ease-in-out
          ${isOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
        `}
        style={{ 
          backgroundColor: 'var(--background-secondary)',
          borderColor: 'var(--border-color)',
          color: 'var(--text-primary)'
        }}>
        <div className="flex flex-col h-full">
          {/* New Chat Button */}
          <div className="p-4 border-b border-policypal-border">
                          <button
                onClick={onCreateNewChat}
                className="w-full flex items-center gap-3 p-3 text-left border border-policypal-border rounded-lg hover:bg-policypal-light transition-colors"
              >
                <Plus className="w-4 h-4" />
                <span>New Policy Chat</span>
              </button>
          </div>

          {/* Chat List */}
          <div className="flex-1 overflow-y-auto">
            <div className="p-2">
              {chats.map((chat) => (
                <div
                  key={chat.id}
                                     className={`
                     relative group flex items-center gap-3 p-3 rounded-lg cursor-pointer transition-colors
                     ${currentChatId === chat.id 
                       ? 'bg-policypal-light text-white' 
                       : 'hover:bg-policypal-light text-gray-300 hover:text-white'
                     }
                   `}
                  onClick={() => onChatSelect(chat.id)}
                >
                  <MessageSquare className="w-4 h-4 flex-shrink-0" />
                  <div className="flex-1 min-w-0">
                    {editingChatId === chat.id ? (
                      <div className="flex items-center gap-1">
                        <input
                          type="text"
                          value={editTitle}
                          onChange={(e) => setEditTitle(e.target.value)}
                          onKeyDown={handleKeyPress}
                          className="flex-1 bg-policypal-light border border-policypal-border rounded px-2 py-1 text-sm text-white focus:outline-none focus:border-policypal-blue"
                          autoFocus
                        />
                        <button
                          onClick={() => handleSaveEdit(chat.id)}
                          className="p-1 hover:bg-green-600 rounded transition-colors"
                          title="Save"
                        >
                          <Check className="w-3 h-3 text-green-400" />
                        </button>
                        <button
                          onClick={handleCancelEdit}
                          className="p-1 hover:bg-red-600 rounded transition-colors"
                          title="Cancel"
                        >
                          <X className="w-3 h-3 text-red-400" />
                        </button>
                      </div>
                    ) : (
                      <>
                        <div className="truncate text-sm font-medium">
                          {chat.title}
                        </div>
                        {chat.messages.length > 0 && (
                          <div className="text-xs text-gray-400 truncate">
                            {formatTime(chat.messages[chat.messages.length - 1].timestamp)}
                          </div>
                        )}
                      </>
                    )}
                  </div>

                                     {/* Action Buttons */}
                   <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-all">
                     {editingChatId !== chat.id && (
                       <button
                         onClick={(e) => handleEditChat(chat.id, chat.title, e)}
                         className="p-1 rounded hover:bg-blue-600 transition-colors"
                         title="Rename chat"
                       >
                         <Edit2 className="w-3 h-3 text-blue-400" />
                       </button>
                     )}
                     <button
                       onClick={(e) => handleDeleteChat(chat.id, e)}
                       className={`
                         p-1 rounded hover:bg-red-600 transition-all
                         ${showDeleteConfirm === chat.id ? 'bg-red-600' : ''}
                       `}
                       title="Delete chat"
                     >
                       <Trash2 className="w-3 h-3" />
                     </button>
                   </div>
                </div>
              ))}
            </div>
          </div>

          {/* Bottom Section */}
                     <div className="p-4 border-t border-policypal-border">
            <div className="space-y-2">
              <button 
                onClick={() => setIsProfileOpen(true)}
                className="w-full flex items-center gap-3 p-3 text-left text-gray-300 hover:bg-policypal-light rounded-lg transition-colors"
              >
                <User className="w-4 h-4" />
                <span className="text-sm">My Profile</span>
              </button>
              <button 
                onClick={() => setIsSettingsOpen(true)}
                className="w-full flex items-center gap-3 p-3 text-left text-gray-300 hover:bg-policypal-light rounded-lg transition-colors"
              >
                <SettingsIcon className="w-4 h-4" />
                <span className="text-sm">Settings</span>
              </button>
              <button 
                onClick={onSignOut}
                className="w-full flex items-center gap-3 p-3 mt-4 text-left text-red-400 hover:bg-red-500 hover:text-white border border-red-400 rounded-lg transition-colors"
              >
                <LogOut className="w-4 h-4" />
                <span className="text-sm font-medium">Sign Out</span>
              </button>
             </div>

             <div className="mt-4 pt-4 border-t border-policypal-border">
               <p className="text-xs text-gray-400 text-center">
                 PolicyPal v1.0.0
               </p>
             </div>
          </div>
        </div>
      </div>
      {/* Profile Modal */}
      <Profile isOpen={isProfileOpen} onClose={() => setIsProfileOpen(false)} />

      {/* Settings Modal */}
      <SettingsModal isOpen={isSettingsOpen} onClose={() => setIsSettingsOpen(false)} />
    </>
  );
};

export default Sidebar;
