import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ChatArea from './components/ChatArea';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import { Send } from 'lucide-react';
import { ThemeProvider } from './context/ThemeContext';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [chats, setChats] = useState([
    {
      id: 1,
      title: 'New Policy Chat',
      messages: []
    }
  ]);
  const [currentChatId, setCurrentChatId] = useState(1);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  useEffect(() => {
    const user = localStorage.getItem('policypal_currentUser');
    if (user) {
      setCurrentUser(JSON.parse(user));
      setIsAuthenticated(true);
    }
  }, []);

  const handleSignIn = (user) => {
    setCurrentUser(user);
    setIsAuthenticated(true);
  };

  const handleSignUp = (user) => {
    setCurrentUser(user);
    setIsAuthenticated(true);
    setShowSignUp(false);
  };

  const handleSignOut = () => {
    localStorage.removeItem('policypal_currentUser');
    setCurrentUser(null);
    setIsAuthenticated(false);
    setChats([]);
  };

  const currentChat = chats.find(chat => chat.id === currentChatId);

  const createNewChat = () => {
    const newChat = {
      id: Date.now(),
      title: 'New Policy Chat',
      messages: []
    };
    setChats(prev => [newChat, ...prev]);
    setCurrentChatId(newChat.id);
    setInputMessage('');
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      content: inputMessage,
      role: 'user',
      timestamp: new Date().toISOString()
    };

    // Update current chat with user message
    setChats(prev => prev.map(chat => 
      chat.id === currentChatId 
        ? { 
            ...chat, 
            messages: [...chat.messages, userMessage],
            title: chat.messages.length === 0 ? inputMessage.slice(0, 30) + '...' : chat.title
          }
        : chat
    ));

    setInputMessage('');
    setIsLoading(true);

    // Get AI response from backend
    try {
      const aiResponse = await generateAIResponse(inputMessage);
      const aiMessage = {
        id: Date.now() + 1,
        content: aiResponse,
        role: 'assistant',
        timestamp: new Date().toISOString()
      };

      setChats(prev => prev.map(chat => 
        chat.id === currentChatId 
          ? { ...chat, messages: [...chat.messages, aiMessage] }
          : chat
      ));
    } catch (error) {
      console.error('Error getting AI response:', error);
      const errorMessage = {
        id: Date.now() + 1,
        content: "I apologize, but I'm having trouble processing your request right now. Please try again later.",
        role: 'assistant',
        timestamp: new Date().toISOString()
      };

      setChats(prev => prev.map(chat => 
        chat.id === currentChatId 
          ? { ...chat, messages: [...chat.messages, errorMessage] }
          : chat
      ));
    } finally {
      setIsLoading(false);
    }
  };

  const generateAIResponse = async (userInput) => {
    try {
      // Get user profile
      const userId = localStorage.getItem('userId') || 'default';
      const profileResponse = await fetch(`http://localhost:5000/api/profile/${userId}`);
      const profile = await profileResponse.json();

      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userInput,
          profile: profile // Include user profile in chat request
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to get response from AI');
      }

      const data = await response.json();
      return data.response;
    } catch (error) {
      console.error('Error calling AI API:', error);
      return `I apologize, but I'm having trouble connecting to my AI service right now. Please try again later. Error: ${error.message}`;
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const deleteChat = (chatId) => {
    setChats(prev => prev.filter(chat => chat.id !== chatId));
    if (currentChatId === chatId) {
      const remainingChats = chats.filter(chat => chat.id !== chatId);
      if (remainingChats.length > 0) {
        setCurrentChatId(remainingChats[0].id);
      } else {
        createNewChat();
      }
    }
  };

  const renameChat = (chatId, newTitle) => {
    setChats(prev => prev.map(chat => 
      chat.id === chatId ? { ...chat, title: newTitle } : chat
    ));
  };

      return (
    <ThemeProvider>
      {!isAuthenticated ? (
        showSignUp ? (
          <SignUp 
            onSignUp={handleSignUp}
            onSwitchToSignIn={() => setShowSignUp(false)}
          />
        ) : (
          <SignIn 
            onSignIn={handleSignIn}
            onSwitchToSignUp={() => setShowSignUp(true)}
          />
        )
      ) : (
        <div className="flex h-screen" style={{ backgroundColor: 'var(--background-primary)' }}>
        {/* Sidebar */}
               <Sidebar
         chats={chats}
         currentChatId={currentChatId}
         onChatSelect={setCurrentChatId}
         onCreateNewChat={createNewChat}
         onDeleteChat={deleteChat}
         onRenameChat={renameChat}
         isOpen={sidebarOpen}
         onToggle={() => setSidebarOpen(!sidebarOpen)}
         onSignOut={handleSignOut}
       />

        {/* Main Chat Area */}
        <div className="flex-1 flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-center p-4 border-b border-policypal-border bg-policypal-dark">
            <h1 className="text-lg font-semibold">PolicyPal</h1>
          </div>

        {/* Chat Messages */}
        <ChatArea 
          messages={currentChat?.messages || []} 
          isLoading={isLoading}
        />

        {/* Input Area */}
        <div className="p-4 border-t border-policypal-border bg-policypal-dark">
          <div className="max-w-4xl mx-auto">
            <div className="relative">
              <textarea
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask about policies, regulations, or compliance..."
                className="w-full p-4 pr-12 bg-policypal-light border border-policypal-border rounded-lg resize-none focus:outline-none focus:border-policypal-blue transition-colors"
                rows="1"
                style={{ minHeight: '52px', maxHeight: '200px' }}
              />
              <button
                onClick={sendMessage}
                disabled={!inputMessage.trim() || isLoading}
                className="absolute right-2 top-2 p-2 bg-policypal-blue hover:bg-blue-600 disabled:bg-gray-600 disabled:cursor-not-allowed rounded-md transition-colors"
              >
                <Send className="w-4 h-4" />
              </button>
            </div>
            <p className="text-xs text-gray-400 mt-2 text-center">
              PolicyPal. Your AI-powered policy assistant. Get instant guidance on policies, regulations, and compliance matters.
            </p>
          </div>
        </div>
      </div>
    </div>
      )}
    </ThemeProvider>
  );
}

export default App;
