import React, { useState, useEffect, useRef } from 'react';
import Sidebar from './components/Sidebar';
import ChatArea from './components/ChatArea';
import { Plus, Send, Bot, User } from 'lucide-react';

function App() {
  const [chats, setChats] = useState([
    {
      id: 1,
      title: 'New Chat',
      messages: []
    }
  ]);
  const [currentChatId, setCurrentChatId] = useState(1);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const currentChat = chats.find(chat => chat.id === currentChatId);

  const createNewChat = () => {
    const newChat = {
      id: Date.now(),
      title: 'New Chat',
      messages: []
    };
    setChats(prev => [newChat, ...prev]);
    setCurrentChatId(newChat.id);
    setInputMessage('');
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      content: inputMessage,
      role: 'user',
      timestamp: new Date().toISOString()
    };

    // Update current chat with user message
    setChats(prev => prev.map(chat => 
      chat.id === currentChatId 
        ? { 
            ...chat, 
            messages: [...chat.messages, userMessage],
            title: chat.messages.length === 0 ? inputMessage.slice(0, 30) + '...' : chat.title
          }
        : chat
    ));

    setInputMessage('');
    setIsLoading(true);

    // Simulate AI response
    setTimeout(() => {
      const aiMessage = {
        id: Date.now() + 1,
        content: generateAIResponse(inputMessage),
        role: 'assistant',
        timestamp: new Date().toISOString()
      };

      setChats(prev => prev.map(chat => 
        chat.id === currentChatId 
          ? { ...chat, messages: [...chat.messages, aiMessage] }
          : chat
      ));

      setIsLoading(false);
    }, 1000 + Math.random() * 2000);
  };

  const generateAIResponse = (userInput) => {
    const responses = [
      "That's an interesting question! Let me think about that...",
      "I understand what you're asking. Here's my perspective on this matter.",
      "Great question! This is a complex topic that involves several factors.",
      "I appreciate you bringing this up. Let me provide some insights on this.",
      "That's a thoughtful inquiry. Here's what I can tell you about this subject.",
      "Interesting point! Let me break this down for you in a clear way.",
      "I see what you're getting at. This is something worth exploring further.",
      "Good question! Let me share some information that might be helpful.",
      "I understand your curiosity about this. Here's what I know on the topic.",
      "That's a valid question. Let me provide some context and explanation."
    ];
    
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const deleteChat = (chatId) => {
    setChats(prev => prev.filter(chat => chat.id !== chatId));
    if (currentChatId === chatId) {
      const remainingChats = chats.filter(chat => chat.id !== chatId);
      if (remainingChats.length > 0) {
        setCurrentChatId(remainingChats[0].id);
      } else {
        createNewChat();
      }
    }
  };

  return (
    <div className="flex h-screen bg-chatgpt-gray">
      {/* Sidebar */}
      <Sidebar
        chats={chats}
        currentChatId={currentChatId}
        onChatSelect={setCurrentChatId}
        onCreateNewChat={createNewChat}
        onDeleteChat={deleteChat}
        isOpen={sidebarOpen}
        onToggle={() => setSidebarOpen(!sidebarOpen)}
      />

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-chatgpt-border bg-chatgpt-dark">
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="p-2 hover:bg-chatgpt-light rounded-md transition-colors"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
          <h1 className="text-lg font-semibold">ChatGPT Clone</h1>
          <div className="w-10"></div>
        </div>

        {/* Chat Messages */}
        <ChatArea 
          messages={currentChat?.messages || []} 
          isLoading={isLoading}
        />

        {/* Input Area */}
        <div className="p-4 border-t border-chatgpt-border bg-chatgpt-dark">
          <div className="max-w-4xl mx-auto">
            <div className="relative">
              <textarea
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Send a message..."
                className="w-full p-4 pr-12 bg-chatgpt-light border border-chatgpt-border rounded-lg resize-none focus:outline-none focus:border-chatgpt-green transition-colors"
                rows="1"
                style={{ minHeight: '52px', maxHeight: '200px' }}
              />
              <button
                onClick={sendMessage}
                disabled={!inputMessage.trim() || isLoading}
                className="absolute right-2 top-2 p-2 bg-chatgpt-green hover:bg-green-600 disabled:bg-gray-600 disabled:cursor-not-allowed rounded-md transition-colors"
              >
                <Send className="w-4 h-4" />
              </button>
            </div>
            <p className="text-xs text-gray-400 mt-2 text-center">
              ChatGPT Clone. Free Research Preview. Our goal is to make AI systems more natural and safe to interact with.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
